SET SQL_MODE = "NO_AUTO_VALUE_ON_ZERO";
SET time_zone = "+00:00";

CREATE TABLE IF NOT EXISTS `tv_cat` (
  `cat_id` int(11) NOT NULL AUTO_INCREMENT,
  `cat_name` varchar(128) COLLATE utf8_turkish_ci NOT NULL,
  `cat_desc` varchar(128) COLLATE utf8_turkish_ci NOT NULL,
  `cat_icon` varchar(128) COLLATE utf8_turkish_ci NOT NULL,
  `cat_parent` int(11) NOT NULL,
  `cat_order` int(11) NOT NULL,
  `cat_status` int(1) NOT NULL,
  `cat_forum` int(11) NOT NULL,
  `createtar` int(11) NOT NULL,
  `changetar` int(11) NOT NULL,
  PRIMARY KEY (`cat_id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COLLATE=utf8_turkish_ci AUTO_INCREMENT=28 ;


CREATE TABLE IF NOT EXISTS `tv_files` (
  `file_id` int(11) NOT NULL AUTO_INCREMENT,
  `file_name` varchar(128) COLLATE utf8_turkish_ci NOT NULL,
  `file_desc` varchar(256) COLLATE utf8_turkish_ci NOT NULL,
  `file_creator` varchar(128) COLLATE utf8_turkish_ci NOT NULL,
  `file_version` varchar(128) COLLATE utf8_turkish_ci NOT NULL,
  `file_beta_version` varchar(128) COLLATE utf8_turkish_ci NOT NULL,
  `file_longdesc` longtext COLLATE utf8_turkish_ci NOT NULL,
  `file_longdesc_note` text COLLATE utf8_turkish_ci NOT NULL,
  `file_longdesc_author` varchar(128) COLLATE utf8_turkish_ci NOT NULL,
  `file_longdesc_link` varchar(128) COLLATE utf8_turkish_ci NOT NULL,
  `file_ssurl` text COLLATE utf8_turkish_ci NOT NULL,
  `file_dlurl` text COLLATE utf8_turkish_ci NOT NULL,
  `file_beta_dlurl` text COLLATE utf8_turkish_ci NOT NULL,
  `file_posticon` varchar(128) COLLATE utf8_turkish_ci NOT NULL,
  `file_screen` text COLLATE utf8_turkish_ci NOT NULL,
  `file_rss_version` varchar(256) COLLATE utf8_turkish_ci NOT NULL,
  `file_nonrss_version` varchar(256) COLLATE utf8_turkish_ci NOT NULL,
  `file_video` text COLLATE utf8_turkish_ci NOT NULL,
  `file_catid` int(11) NOT NULL,
  `file_status` int(11) NOT NULL,
  `createtar` int(11) NOT NULL,
  `changetar` int(11) NOT NULL,
  PRIMARY KEY (`file_id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COLLATE=utf8_turkish_ci AUTO_INCREMENT=93 ;


CREATE TABLE IF NOT EXISTS `tv_meta` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `meta_name` varchar(128) CHARACTER SET latin5 NOT NULL,
  `meta_value` text CHARACTER SET latin5 NOT NULL,
  `createtar` int(11) NOT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COLLATE=utf8_turkish_ci AUTO_INCREMENT=5 ;


CREATE TABLE IF NOT EXISTS `tv_news` (
  `news_id` int(11) NOT NULL AUTO_INCREMENT,
  `news_fileid` int(11) NOT NULL,
  `news_link` text COLLATE utf8_turkish_ci NOT NULL,
  `news_title` text COLLATE utf8_turkish_ci NOT NULL,
  `news_desc` text COLLATE utf8_turkish_ci NOT NULL,
  `news_text` text COLLATE utf8_turkish_ci NOT NULL,
  `news_status` int(11) NOT NULL,
  `createtar` int(11) NOT NULL,
  `changetar` int(11) NOT NULL,
  PRIMARY KEY (`news_id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COLLATE=utf8_turkish_ci AUTO_INCREMENT=108 ;


CREATE TABLE IF NOT EXISTS `tv_users` (
  `user_id` int(11) NOT NULL AUTO_INCREMENT,
  `user_username` varchar(128) COLLATE utf8_turkish_ci NOT NULL,
  `user_password` varchar(128) COLLATE utf8_turkish_ci NOT NULL,
  `user_email` varchar(128) COLLATE utf8_turkish_ci NOT NULL,
  `user_status` int(1) NOT NULL DEFAULT '0',
  `createtar` int(11) NOT NULL,
  `changetar` int(11) NOT NULL,
  PRIMARY KEY (`user_id`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COLLATE=utf8_turkish_ci AUTO_INCREMENT=14 ;

